import React from "react";
import "isomorphic-fetch";
import Fonts from './Fonts';

import StatCard from './stat-card';
import "../scss/main.scss";


interface PokemonListState {
  data: any;
}

class PokemonList extends React.Component<{}, PokemonListState> {
    constructor(props) {
        super(props);
        this.state = { data: []};
    }

    async getPokemons(url) {
        const resp = await fetch(url);
        const respJson = await resp.json();
        const results = respJson.results;
        await this.setState({
            data: [...this.state.data, ...results]
        });

        if (results.next) {
            this.getPokemons(results.next);
        }
    }

    componentDidMount() {
        Fonts();
        this.getPokemons(`https://pokeapi.co/api/v2/pokemon/`);
    }

    render() {
        const { data } = this.state;

        return (
            <div className="pokemon-list">
                {data.map(entry => 
                <div>
                    <h3>{entry.name}</h3>
                    <img src={entry.sprites.front_default}/>
                    <p> weight: {entry.weight}</p>
                </div>)}
            </div>
        );
    }
}

export default PokemonList;
