import React from "react";
import Elevator from '../elevator-page/elevator';
import {ElevatorProps, ElevatorDirection} from '../elevator-page/elevator-types';

interface User {
    waiting: boolean;
    startFloor: number;
    endFloor: number;
}

interface ElevatorSystemState {
    elevators: ElevatorProps[];
    users: User[];
}

class ElevatorSystem extends React.Component<{}, ElevatorSystemState> {

    constructor(props) {
        super(props);

        const totalFloors = 10;

        this.state = {
            elevators: [{floor: 1, direction: ElevatorDirection.Up, stops: []}],
            users: []
        }
    }

    render() {
        const {elevators, users, totalFloors} = this.state;
        const elevatorShaftStyles = {height: `${100 *totalFloors}px`};

        return (<div className='elevator-system'>
            <div className="elevator-system--elevators">
                {elevators.map(el => 
                <div className="elevator__shaft" styles={elevatorShaftStyles}>
                    <Elevator {...el}/>
                </div>
                )}
            </div>
            <div className="elevator-system--users">
                {users.map(el => 
                    <div className="elevator-system--user"></div>
                )}
            </div>
        </div>)
    }
}

export default ElevatorSystem;
