import React from "react";
import Elevator from '../elevator-page/elevator';
import {ElevatorProps, ElevatorDirection} from '../elevator-page/elevator-types';
import { number } from "prop-types";

interface User {
    origin: number;
    inElevator?: number;
    onFloor?: number;
    destination: number;
    direction: ElevatorDirection;
}

interface ElevatorSystemState {
    elevators: ElevatorProps[];
    users: User[];
    totalFloors: number;
}

class ElevatorSystem extends React.Component<{}, ElevatorSystemState> {
    constructor(props) {
        super(props);

        this.state = {
            elevators: [
                {floor: 1, direction: ElevatorDirection.Up, stops: []},
                {floor: 4, direction: ElevatorDirection.Down, stops: []}
            ],
            users: [{
                origin: 2,
                onFloor: 2,
                destination: 3,
                direction: ElevatorDirection.Up,
            }, {
                origin: 1,
                inElevator: 1,
                destination: 4,
                direction: ElevatorDirection.Up,
            }],
            totalFloors: 5
        }
    }

    elevatorStep() {
        let {elevators, users, totalFloors} = this.state;

        elevators.forEach((el, i) => {
            // MOVEMENT
            // if at top or bottom, turn around
            if(el.floor === totalFloors) {
                el.direction = ElevatorDirection.Down;
            }
            if(el.floor === 1) {
                el.direction = ElevatorDirection.Up;
            }

            // take next step
            el.floor = el.direction === ElevatorDirection.Up ? el.floor++ : el.floor--;

            // USERS
            // let off users
            users.forEach(u => {
                //let riders off
                if (u.inElevator === i && el.floor === u.destination) {
                    u.inElevator = null;
                    u.onFloor = u.destination;
                }    
                //let riders on
                if (u.onFloor === i && el.direction === u.direction) {
                    u.onFloor = null;
                    u.inElevator = i;
                }
            });
        });

        this.setState({
            elevators,
            users
        });
    }

    render() {
        const {elevators, users, totalFloors} = this.state;
        const elevatorShaftStyles = {height: `${100 *totalFloors}px`};
        const floors = new Array(totalFloors).fill(0);

        const peopleInElevators = users.filter(u => u.inElevator);
        const peopleWaiting = users.filter(u => u.onFloor);

        return (<div className='elevator-system'>
            <button onClick={this.elevatorStep}>Next Step</button>
            <div className="elevator-system__elevators">
                {elevators.map((el, i) => 
                    <div className="elevator__shaft" style={elevatorShaftStyles}>
                        <Elevator {...el}>
                            {peopleInElevators
                                .filter(p => p.inElevator === i)
                                .map(p => 
                                    <div className="elevator__rider">
                                        {`${p.origin}\u21e2${p.destination}`}
                                    </div>)}
                        </Elevator>
                    </div>
                )}
            </div>
            <div className="elevator-system__floors" style={elevatorShaftStyles}>
                {floors.map((floor, i) => 
                    <div className="elevator-system__floor">
                        {peopleWaiting
                        .filter(p => p.onFloor === i)
                        .map(p => 
                            <div className="elevator__rider">
                                {`${p.origin}\u21e2${p.destination}`}
                            </div>)}                       
                    </div>
                )}
            </div>
        </div>)
    }
}

export default ElevatorSystem;
